declare const appMethods: readonly ["checkout", "copy", "delete", "get", "head", "lock", "merge", "mkactivity", "mkcol", "move", "m-search", "notify", "options", "patch", "post", "purge", "put", "report", "search", "subscribe", "trace", "unlock", "unsubscribe", "route", "use", "all"];
type AppMethods = typeof appMethods[number];
type App = {
    [k in AppMethods]: (...args: any[]) => any;
};
/**
 * Added support async/await to express.
 */
declare const asyncify: <T extends App>(app: T) => T;
export = asyncify;
